script:
frappe.listview_settings['Buyback Price Master'] = {
    onload(listview) {

        if (listview.buttons_added) return;
        listview.buttons_added = true;

        // Download button
        listview.page.add_inner_button('Download Template', () => {
            window.open('/api/method/buyback.api.download_buyback_template');
        });

        // Upload button
        listview.page.add_inner_button('Upload CSV', () => {

            new frappe.ui.FileUploader({
                allow_multiple: false,
                on_success(file) {

                    frappe.call({
                        method: "buyback.api.upload_buyback_csv",
                        args: { file_url: file.file_url },
                        callback: () => {
                            frappe.msgprint("Upload complete");
                            listview.refresh();
                        }
                    });

                }
            });

        });

    }
};

frappe.ui.form.on('Buyback Request', {
    refresh(frm) {
        apply_mode(frm);
    },
    mode(frm) {
        apply_mode(frm);
    }
});

function apply_mode(frm) {

    let is_buyback = frm.doc.mode === "BuyBack";

    frm.toggle_display("buyback_section", is_buyback);
    frm.toggle_display("diagnosis_section", !is_buyback);

    frm.dashboard.clear_headline();
    frm.dashboard.set_headline(
        is_buyback ? "BuyBack Mode Active" : "Diagnosis Mode Active"
    );
}

frappe.ui.form.on('Buyback Request', {
    refresh(frm) {
        if (frm.is_new()) calc_price(frm);
    },

    item_name: calc_price,
    usage_key: calc_price,
    grade: calc_price
});

function calc_price(frm) {

    // stop if fields missing
    if (!frm.doc.item_name || !frm.doc.usage_key || !frm.doc.grade) {
        frm.set_value('buyback_price', 0);
        frm.set_value('final_buyback_amount', 0);
        return;
    }

    let key = `${frm.doc.grade}_grade_${frm.doc.usage_key}`;
    console.log("PRICE KEY →", key);

    frappe.db.get_doc('Buyback Price Master', frm.doc.item_name)
        .then(doc => {

            let price = doc[key] || 0;

            console.log("FETCHED PRICE →", price);

            frm.set_value('buyback_price', price);
            frm.set_value('final_buyback_amount', price);
        })
        .catch(err => {
            console.error("Price fetch error:", err);
            frm.set_value('buyback_price', 0);
            frm.set_value('final_buyback_amount', 0);
        });
}

frappe.ui.form.on('Buyback Request', {
    refresh: function(frm) {

        if (frm.is_new()) {
            frm.set_value('buyback_request_staff', frappe.session.user);
        }
    }
});

frappe.ui.form.on('Buyback Request', {

    refresh(frm) {
        show_preview(frm);
    },

    customer_image(frm) {
        show_preview(frm);
    }
});

function show_preview(frm) {

    if (!frm.doc.customer_image) {
        frm.set_value('customer_image_preview', '');
        return;
    }

    let url = window.location.origin + frm.doc.customer_image;

    frm.set_value('customer_image_preview', url);
}

frappe.ui.form.on('Buyback Request', {
    payment_mode: function(frm) {
        payment_ui(frm);
    },
    refresh: function(frm) {
        payment_ui(frm);
    }
});

function payment_ui(frm) {

    // hide everything first
    frm.toggle_display([
        'cash_notes',
        'account_holder_name',
        'branch',
        'bank_name',
        'ifsc_code',
        'upi_id',
        'transaction_proof'
    ], false);

    // always show amount
    frm.toggle_display('final_buyback_amount', true);

    if (!frm.doc.payment_mode) return;

    frappe.db.get_doc('Payment Method Master', frm.doc.payment_mode)
        .then(doc => {

            frm.set_value('payment_mode_name', doc.payment_method);

            // CASH
            if (doc.payment_method === "Cash") {
                frm.toggle_display('cash_notes', true);
            }

            // BANK
            else if (doc.payment_method === "Bank Transfer") {
                frm.toggle_display([
                    'account_holder_name',
                    'branch',
                    'bank_name',
                    'ifsc_code',
                     'transaction_proof'
                ], true);
            }

            // UPI
            else if (doc.payment_method === "UPI") {
                frm.toggle_display([
                    'upi_id',
                    'transaction_proof'
                ], true);
            }

        });
}
